import java.nio.charset.StandardCharsets
import java.util.HashMap
import java.util.regex.Pattern
import java.util.UUID

// Define max chunk length in characters.
// Adjust based on your embedding model's token limit (e.g., for ~1500 tokens, ~6000 characters is a rough estimate).
// Always test with your specific model.
final int MAX_CHUNK_LENGTH = 500

FlowFile flowFile = session.get()
if (flowFile != null) {
    String originalId = null
    String originalTitle = ""
    String originalContent = ""

    try {
        Map<String, String> originalAttrs = new HashMap<>(flowFile.getAttributes())

        // --- COMPREHENSIVE FIX START: Explicitly cast all attribute retrievals and GStrings to String ---
        // Ensure originalId is a String, handling potential GStringImpl from attributes
        originalId = originalAttrs.get("id") as String 
        originalTitle = (originalAttrs.get("title") ?: "") as String 
        originalContent = (originalAttrs.get("content") ?: "") as String 

        if (originalId == null || originalId.trim().isEmpty()) {
            originalId = UUID.randomUUID().toString() as String // Ensure UUID is a String
            log.debug("No original 'id' attribute found. Generated new ID: ${originalId}")
        }

        if (originalContent.trim().isEmpty()) {
            log.warn("FlowFile ${originalId} has empty or whitespace-only content, skipping chunking.")
            session.transfer(flowFile, REL_SUCCESS)
            return
        }

        // --- Simple Sentence-based Chunking ---
        Pattern sentencePattern = Pattern.compile("(?<!\\w\\.\\w.)(?<![A-Z][a-z]\\.)(?<=\\.|\\?|\\!)\\s")
        List<String> sentences = sentencePattern.split(originalContent).collect { it.trim() }.findAll { !it.isEmpty() }

        List<String> chunks = []
        List<String> currentChunkSentences = []
        int currentChunkLength = 0

        for (String sentence : sentences) {
            // sentence is already String from split()
            int estimatedNewChunkLen = currentChunkLength + sentence.length() + originalTitle.length() + 50

            if (estimatedNewChunkLen < MAX_CHUNK_LENGTH) {
                currentChunkSentences.add(sentence)
                currentChunkLength += sentence.length() + 1
            } else {
                if (!currentChunkSentences.isEmpty()) {
                    // Ensure join result is a String before adding to chunks List<String>
                    chunks.add(currentChunkSentences.join(" ") as String) 
                }
                currentChunkSentences = [sentence] // sentence is String
                currentChunkLength = sentence.length() + 1
            }
        }

        if (!currentChunkSentences.isEmpty()) {
            // Ensure join result is a String before adding to chunks List<String>
            chunks.add(currentChunkSentences.join(" ") as String) 
        }
        // --- End Simple Chunking ---

        if (chunks.isEmpty()) {
            log.warn("No chunks generated for FlowFile ${originalId}. Content might be too short or chunking logic failed.")
            session.transfer(flowFile, REL_SUCCESS)
            return
        }

        for (int i = 0; i < chunks.size(); i++) {
            String chunkText = chunks.get(i) as String // Ensure chunkText is a String
            FlowFile childFlowFile = session.create(flowFile)

            // Fixed: Added 'inputStream' to the closure signature for StreamCallback
            childFlowFile = session.write(childFlowFile, { inputStream, outputStream -> 
                outputStream.write(chunkText.getBytes(StandardCharsets.UTF_8))
            } as StreamCallback)

            Map<String, String> newAttrs = new HashMap<>(originalAttrs)

            // Ensure values put into Map<String, String> are actual Strings
            newAttrs.put("chunk.number", String.valueOf(i + 1) as String) 
            newAttrs.put("chunk.text", chunkText)
            newAttrs.put("chunk.id", UUID.randomUUID().toString() as String) // Key fix for GStringImpl
            childFlowFile = session.putAllAttributes(childFlowFile, newAttrs)

            session.transfer(childFlowFile, REL_SUCCESS)
        }

        session.remove(flowFile)

    } catch (Exception e) {
        log.error("Failed to chunk FlowFile ${originalId ?: 'unknown'}: ${e.getMessage()}", e)
        session.transfer(flowFile, REL_FAILURE)
    }
}
