import org.apache.nifi.processor.io.InputStreamCallback
import java.nio.charset.StandardCharsets
import java.util.regex.Pattern
import org.apache.nifi.processor.Relationship // Ensure this import is there

def flowFile = session.get()
if (flowFile == null) {
    return
}

def originalFlowFileUuid = flowFile.getAttribute('uuid')

// Define Relationship objects by iterating through available relationships from the context
def successRelationship = null
def failureRelationship = null

// Iterate through the relationships provided by the ProcessContext
// This method (getAvailableRelationships) should be present in all NiFi versions
// as it's fundamental to how processors announce their relationships.
for (Relationship rel : context.getAvailableRelationships()) {
    if (rel.getName() == 'success') {
        successRelationship = rel
    } else if (rel.getName() == 'failure') {
        failureRelationship = rel
    }
    // No 'unmatched' here, as per your last clarification
}

// Perform basic null checks for relationships - these are critical here
if (successRelationship == null) {
    log.error("Relationship 'success' not found in ExecuteScript configuration! Cannot proceed. Removing FlowFile ${originalFlowFileUuid}.")
    session.remove(flowFile)
    return
}
if (failureRelationship == null) {
    log.error("Relationship 'failure' not found in ExecuteScript configuration! Cannot proceed. Removing FlowFile ${originalFlowFileUuid}.")
    session.remove(flowFile)
    return
}

def errorPattern = Pattern.compile(".*ERROR.*")

try {
    def containsError = false
    session.read(flowFile, { inputStream ->
        def content = new String(inputStream.bytes, StandardCharsets.UTF_8)
        if (errorPattern.matcher(content).find()) {
            containsError = true
        }
    } as InputStreamCallback)

    if (containsError) {
        log.info("FlowFile ${originalFlowFileUuid} contains 'ERROR' and will be transferred to 'success'.")
        session.transfer(flowFile, successRelationship)
    } else {
        log.info("FlowFile ${originalFlowFileUuid} does NOT contain 'ERROR' and will be transferred to 'failure'.")
        session.transfer(flowFile, failureRelationship)
    }

} catch (e) {
    log.error("Failed to process FlowFile ${originalFlowFileUuid} due to ${e.getMessage()}", e)
    session.transfer(flowFile, failureRelationship)
}

import org.apache.nifi.processor.io.InputStreamCallback // Import InputStreamCallback
import java.nio.charset.StandardCharsets
import java.util.regex.Pattern

def flowFile = session.get()
if (flowFile == null) {
    return
}

// Regex to identify the start of a new log entry (timestamp pattern)
def timestampPattern = Pattern.compile("^\\d{4}-\\d{2}-\\d{2} \\d{2}:\\d{2}:\\d{2},\\d{3} .*")

// Get the UUID using getAttribute() for logging if needed
def originalFlowFileUuid = flowFile.getAttribute('uuid')

try {
    // Corrected: Use InputStreamCallback
    session.read(flowFile, { inputStream ->
        def content = new String(inputStream.bytes, StandardCharsets.UTF_8)
        def lines = content.split('\n')

        def currentLogEntry = new StringBuilder()
        int entryCount = 0

        lines.eachWithIndex { line, index ->
            if (timestampPattern.matcher(line).find()) {
                if (currentLogEntry.length() > 0) {
                    def newFlowFile = session.create(flowFile) // Copies existing attributes from original FlowFile
                    session.write(newFlowFile, { outputStream ->
                        outputStream.write(currentLogEntry.toString().getBytes(StandardCharsets.UTF_8))
                    } as org.apache.nifi.processor.io.OutputStreamCallback) // Also specify OutputStreamCallback here
                    session.transfer(newFlowFile, REL_SUCCESS)
                    entryCount++
                }
                currentLogEntry = new StringBuilder(line)
            } else {
                currentLogEntry.append('\n').append(line)
            }
        }

        if (currentLogEntry.length() > 0) {
            def newFlowFile = session.create(flowFile)
            session.write(newFlowFile, { outputStream ->
                outputStream.write(currentLogEntry.toString().getBytes(StandardCharsets.UTF_8))
            } as org.apache.nifi.processor.io.OutputStreamCallback) // Also specify OutputStreamCallback here
            session.transfer(newFlowFile, REL_SUCCESS)
            entryCount++
        }

        // Corrected line for logging:
        log.info("Successfully split FlowFile ${originalFlowFileUuid} into ${entryCount} log entries.")

    } as InputStreamCallback) // Corrected: Use InputStreamCallback

    session.remove(flowFile) // Remove the original FlowFile as it's been split

} catch (e) {
    // Corrected line for logging in the catch block:
    log.error("Failed to split FlowFile ${originalFlowFileUuid} due to ${e.getMessage()}", e)
    session.transfer(flowFile, REL_FAILURE)
}

import org.apache.nifi.processor.io.InputStreamCallback
import org.apache.nifi.processor.io.OutputStreamCallback
import java.nio.charset.StandardCharsets
import java.util.regex.Pattern

def flowFile = session.get()
if (!flowFile) {
    return // No FlowFile to process
}

try {
    // 1. Access existing FlowFile attributes
    def processGroupId = flowFile.getAttribute('processgroup.id')
    def processGroupName = flowFile.getAttribute('processgroup.name')

    // 2. Read the content of the FlowFile (the error log line)
    def originalLogLine = new StringBuilder()
    session.read(flowFile, { inputStream ->
        originalLogLine.append(new String(inputStream.bytes, StandardCharsets.UTF_8))
    } as InputStreamCallback)
    def fullErrorMessage = originalLogLine.toString().trim()

    // 3. Define Regex to extract:
    //    Group 1: Timestamp from the beginning of the log line
    //    Group 2: Processor Name
    //    Group 3: Processor ID
    //    Group 4: The specific error message details
    def pattern = ~/(?s)^(\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2},\d{3}) ERROR \[.*?\] .*? (\w+)\[id=([\w-]+)\] (.*)$/
    def matcher = fullErrorMessage =~ pattern

    def extractedTimestamp = "UnknownTimestamp" // Default value
    def processorName = "UnknownProcessor"
    def processorId = "UnknownID"
    def specificErrorMessage = fullErrorMessage // Default to full message if regex fails

    if (matcher.matches()) {
        extractedTimestamp = matcher.group(1) // Captured timestamp
        processorName = matcher.group(2)
        processorId = matcher.group(3)
        specificErrorMessage = fullErrorMessage
    } else {
        log.warn("ExecuteScript: Could not parse timestamp, processor name/ID from log line: {}", fullErrorMessage)
        // If regex fails, the specificErrorMessage defaults to fullErrorMessage, and timestamp/name/id will be "Unknown"
    }

    // 4. Construct the new formatted message with extracted timestamp
    def outputMessage = """ERROR Occured at Timestamp:${extractedTimestamp ?: 'N/A'} 
    Traceback of ERROR->
    Process Group: ${processGroupName ?: 'N/A'}
    Process Group Id: ${processGroupId ?: 'N/A'}
    Processor Name: "${processorName}"
    Processor Id: "${processorId}"
    ERROR Message: "${specificErrorMessage}" """

    // 5. Write the new content back to the FlowFile
    flowFile = session.write(flowFile, { outputStream ->
        outputStream.write(outputMessage.getBytes(StandardCharsets.UTF_8))
    } as OutputStreamCallback)

    // Transfer the FlowFile to the success relationship
    session.transfer(flowFile, REL_SUCCESS)

} catch (e) {
    // Log the error and transfer FlowFile to failure
    log.error("Failed to process flow file with UUID {}: {}", flowFile.getAttribute('uuid'), e.message, e)
    session.transfer(flowFile, REL_FAILURE)
}
